// FilterContext.js
import React, { createContext, useContext, useState } from 'react';

const FilterContext = createContext();

export const FilterProvider = ({ children }) => {
  const [filters, setFilters] = useState([]);
  const [userSearch, setUserSearch] = useState('');

  const applyFilters = (restaurants) => {
    let filtered = [...restaurants];

    if (filters.includes('fastDelivery')) {
      filtered.sort((a, b) => {
        return a.info.sla.deliveryTime - b.info.sla.deliveryTime
      })
      setFilters(filtered)
    }else if (filters.includes('ratinghightolow')) {
      filtered.sort((a, b) => { 
          return a.info
      });
    }

    return filtered;
  };

  return (
    <FilterContext.Provider value={{ filters, setFilters, applyFilters, userSearch, setUserSearch }}>
      {children}
    </FilterContext.Provider>
  );
};

export const useFilters = () => {
  return useContext(FilterContext);
};
