import React , {useState , useEffect} from 'react';
import axios from 'axios';
import '../App.css';
import Shimmer from '../shimmer';
import Locations from './locations';

function Home() {
    let [restaurants , setRestaurants] = useState([])
  let [userSearch , setUserSearch] = useState("")
  let [filteredRestaurents , setFilteredRestaurents] = useState([])
  let [location , setLocation] =  useState({
    lat : "17.4375084",
    long : "78.4482441"
  })

  useEffect(()=>{
    setRestaurants([])
    setFilteredRestaurents([])
    axios.get(`https://www.swiggy.com/dapi/restaurants/list/v5?lat=${location.lat}&lng=${location.long}&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`)
    .then((res)=>{
      setRestaurants(res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants)
    })
  },[location])

  useEffect(()=>{
    let result = restaurants.filter((item , i)=>{
      if(item.info.name.toLowerCase().includes(userSearch.toLowerCase()) == true)
        return true
    })
    setFilteredRestaurents(result)
  },[userSearch])

  const sortByCategory = (category) => {
    let restaurantDataCopy; 
    if (category == "delivery") {
      if(filteredRestaurents.length > 0)
        {
          restaurantDataCopy = [...filteredRestaurents]
      restaurantDataCopy.sort((a, b) => {
        return a.info.sla.deliveryTime - b.info.sla.deliveryTime;
      })
      setFilteredRestaurents(restaurantDataCopy)
    }else{
      restaurantDataCopy = [...restaurants]
      restaurantDataCopy.sort((a, b) => {
        return a.info.sla.deliveryTime - b.info.sla.deliveryTime;
      })
      setRestaurants(restaurantDataCopy)
    }
    }else if(category == "rating") 
      if(filteredRestaurents.length > 0)
        {
          restaurantDataCopy = [...filteredRestaurents]
      restaurantDataCopy.sort((a, b) => {
        return b.info.avgRating - a.info.avgRating;
      })
      setFilteredRestaurents(restaurantDataCopy)
    }else{
      restaurantDataCopy = [...restaurants]
      restaurantDataCopy.sort((a, b) => {
        return b.info.avgRating - a.info.avgRating;
      })
      setRestaurants(restaurantDataCopy)
    }
  }
  return (
    <div className = "container-fluid">
    <div className='row'>
      <div className = "container-fluid">
      <div class = "row">
        <Locations
        setLocations = {setLocation}/>
      </div>
      </div>
    </div>
  </div>
  )
}

export default Home
