import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../components/restaurantspec.css';
import { addToCart, updateQuantity } from './reducer'; // Assuming you have updateQuantity action in reducer
import { useDispatch } from 'react-redux';

function Restaurantspec() {
    const dispatch = useDispatch();
    const [restaurantMenu, setRestaurantMenu] = useState([]);
    const { restaurantId, restaurantName } = useParams();
    const [openAccordion, setOpenAccordion] = useState(0);

    useEffect(() => {
        axios.get(`https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=17.37240&lng=78.43780&restaurantId=${restaurantId}&catalog_qa=undefined&submitAction=ENTER`)
            .then((res) => {
                setRestaurantMenu(res.data?.data?.cards[4]?.groupedCard?.cardGroupMap?.REGULAR?.cards.slice(1));
            });
    }, [restaurantId]);

    const toggleAccordion = (index) => {
        setOpenAccordion(openAccordion === index ? null : index); // Toggle the open state of the accordion
    };

    const handleQuantityChange = (index, quantity) => {
        // Update quantity in local state or Redux store
        // Here we'll just update the Redux store assuming you have `updateQuantity` action in your reducer
        dispatch(updateQuantity({ index, quantity }));
    };

    return (
        <div className="container">
            <h2 className='restaurant-title'>{restaurantName} Menu</h2>
            <div className="accordion" id="accordionExample">
                {restaurantMenu.map((item, i) => {
                    if (item?.card?.card?.itemCards) {
                        return (
                            <div className="accordion-item" key={i}>
                                <h2 className="accordion-header">
                                    <button
                                        className={`accordion-button ${openAccordion === i ? '' : 'collapsed'}`}
                                        type="button"
                                        onClick={() => toggleAccordion(i)}
                                        aria-expanded={openAccordion === i ? 'true' : 'false'}
                                        aria-controls={`collapse${i}`}
                                        data-bs-toggle="collapse"
                                        data-bs-target={`#collapse${i}`}
                                    >
                                        <b className='category'>{item.card.card.title} - {item?.card?.card?.itemCards.length}</b>
                                        <i className={`fas ${openAccordion === i ? 'fa-chevron-up' : 'fa-chevron-down'} accordion-icon`}></i>
                                    </button>
                                </h2>
                                <div
                                    id={`collapse${i}`}
                                    className={`accordion-collapse collapse ${openAccordion === i ? 'show' : ''}`}
                                    aria-labelledby={`heading${i}`}
                                    data-bs-parent="#accordionExample"
                                >
                                    <div className="accordion-body">
                                        <div className="row row-cols-1 row-cols-md-6 g-2">
                                            {item?.card?.card?.itemCards?.map((item, j) => (
                                                <div className="col" key={j}>
                                                    <div className="card">
                                                        <img
                                                            src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_300,h_300,c_fit/${item.card.info.imageId}`}
                                                            className="card-img-top food-pic"
                                                            alt={item.card.info.name}
                                                        />
                                                        <div className="card-body">
                                                            <h5 className="card-title">{item.card.info.name}</h5>
                                                            {item?.card?.info?.ratings?.aggregatedRating?.rating && (
                                                                <p>
                                                                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                                                                        <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                                                                    </svg>
                                                                    {" " + item.card.info.ratings.aggregatedRating.rating}
                                                                    <span style={{ marginLeft: "10px" }}>{item.card.info.ratings.aggregatedRating.ratingCount}</span>
                                                                </p>
                                                            )}
                                                            <p className="card-text">
                                                                <b>
                                                                    Price : &#8377;
                                                                    {
                                                                        item.card.info.price ? (Number(item.card.info.price) / 100).toFixed(2) : item.card.info.defaultPrice ? (Number(item.card.info.defaultPrice) / 100).toFixed(2) : 'N/A'
                                                                    }
                                                                </b>
                                                            </p>

                                                            <p className="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                                                            <div style={{ textAlign: "center" }}>
                                                                <input
                                                                    type="number"
                                                                    value={item.Quantity} // Assuming you have Quantity in item structure
                                                                    onChange={(e) => handleQuantityChange(j, parseInt(e.target.value))}
                                                                />
                                                                <button
                                                                    onClick={() => {
                                                                        dispatch(addToCart({
                                                                            Name: item.card.info.name,
                                                                            Description: item.card.info.description,
                                                                            Price: item.card.info.price ? item.card.info.price / 100 : item.card.info.defaultPrice / 100,
                                                                            Img: `https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_600/${item?.card?.info?.imageId}`
                                                                        }))
                                                                    }}
                                                                    className='menubtn' style={{ borderRadius: "5px" }}>Add to Cart</button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        );
                    }
                    return null;
                })}
            </div>
        </div>
    );
}

export default Restaurantspec;
