import React , {useState , useEffect} from 'react';
import axios from 'axios';

const LocationSelector = ({ handleLocationChange  , handleModalClose}) => {
  
  let [userLocationSearch , setUserLocationSearch] = useState("")
  let [locationSuggestion , setLocationSuggestion] = useState([])  

  useEffect(()=>{
    axios.get(`https://www.swiggy.com/dapi/misc/place-autocomplete?input=${userLocationSearch}&types=`)
    .then((res)=>{
        setLocationSuggestion(res.data.data)
    })

    },[userLocationSearch])
    return (
    <div style={{ textAlign: "center" }}>
      <input
        placeholder='search locations' 
        value = {userLocationSearch}
        onChange={(e)=>{setUserLocationSearch(e.target.value)}}
        name="locations" />
      <br />
      <br />
      {locationSuggestion.map((item , i)=>{
            return 
      })}
      <label>
        <input type="radio" onChange={() => handleLocationChange({ lat: "17.4485835", long: "78.39080349999999" })} name="locations" />
        Madhapur
      </label>
      <br/><br/>
      <button type="button" class="btn btn-danger" onClick={handleModalClose}>Change Location</button>
    </div>
  );
};

export default LocationSelector;
