import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LocationSelector = ({ locations, handleLocationChange, loading }) => {
  return (
    <div>
      {loading ? (
        <p>Loading locations...</p>
      ) : (
        <div style={{ textAlign: "center" }}>
          {locations.map(location => (
            <div key={location.id}>
              <label>
                <input type="radio" onChange={() => handleLocationChange(location)} name="locations" />
                {location.name}
              </label>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default LocationSelector;
