import React , {useState , useEffect} from 'react'
import axios from 'axios'

function SearchDishes() {

  const [dishName , setDishName] = useState("")
  const [dishes , setDishes] = useState([])

  useEffect(()=>{
    if(dishName.length > 2)
    {
      axios.get(`https://www.swiggy.com/dapi/restaurants/search/v3?lat=17.372040&lng=78.43780
      &str=${dishName}&trackingId=undefined&submitAction=SUGGESTION&queryUniqueId=e8f976e5-270e-3d4d-aca0-573a2513c73a`)
      .then((res)=>{
        if(res.data.data?.cards[1]?.groupedCard?.cardGroupMap?.DISH){
          setDishes(res.data.data?.cards[1]?.groupedCard?.cardGroupMap.DISH.cards.slice(1))
        }
    })
  }
  },[dishName])

  console.log(dishes)

  return (
    <div>
      <div style={{textAlign : "center"}}>
        <h2>SearchDishes</h2>
        <input type = "text" value = {dishName}
        onChange = {(e)=>{
          setDishName(e.target.value)
        }}
        placeholder='Search the dishes'/>
      </div><br/>
      <div class="row row-cols-1 row-cols-md-1 g-1 m-3">
        {dishes.map((item ,i)=>{
          return  <div class="col">
            <div class="card h-100 p-4">
              <h3 class="card-title">{item?.card?.card?.restaurant?.info?.name}</h3>
              <p>Rating , Time to deliver <button>View Restaurant</button></p>
              <div className='row'>
                <div className = "col-6">
              <img src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_600/${item?.card?.card?.info?.imageId}`} class="card-img-top h-10" alt="..."/>
              </div>
              <div class="card-body col-6">
                <h5 class="card-title">{item?.card?.card?.info?.name}</h5>
                <p class="card-text">{item?.card?.card?.info?.description}</p>
                <button> Add to cart</button>
              </div>
              </div>
            </div>
          </div>
        })}
      </div>
    </div>
  )
}

export default SearchDishes
