import React, { useState, useEffect } from 'react';
import axios from 'axios';
import prevarrow from './images/prev-arrow.png';
import nextarrow from './images/white-next-arrow.png';
import './App.css';
import Shimmer from './shimmer';

function App() {
  let [restaurants, setRestaurants] = useState([]);
  let [userSearch, setUserSearch] = useState('');
  let [filteredRestaurants, setFilteredRestaurants] = useState([]);
  let [location, setLocation] = useState({
    lat: '17.4375084',
    long: '78.4482441'
  });

  useEffect(() => {
    axios
      .get(
        `https://www.swiggy.com/dapi/restaurants/list/v5?lat=${location.lat}&lng=${location.long}&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`
      )
      .then((res) => {
        setRestaurants(res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
      });
  }, [location]);

  useEffect(() => {
    let result = restaurants.filter((item) => {
      if (item.info.name.toLowerCase().includes(userSearch.toLowerCase()) === true) return true;
    });
    setFilteredRestaurants(result);
  }, [userSearch]);

  const sortByCategory = (category) => {
    let restaurantDataCopy;
    if (category === 'delivery') {
      if (filteredRestaurants.length > 0) {
        restaurantDataCopy = [...filteredRestaurants];
        restaurantDataCopy.sort((a, b) => {
          return a.info.sla.deliveryTime - b.info.sla.deliveryTime;
        });
        setFilteredRestaurants(restaurantDataCopy);
      } else {
        restaurantDataCopy = [...restaurants];
        restaurantDataCopy.sort((a, b) => {
          return a.info.sla.deliveryTime - b.info.sla.deliveryTime;
        });
        setRestaurants(restaurantDataCopy);
      }
    } else if (category === 'rating') {
      if (filteredRestaurants.length > 0) {
        restaurantDataCopy = [...filteredRestaurants];
        restaurantDataCopy.sort((a, b) => {
          return b.info.avgRating - a.info.avgRating;
        });
        setFilteredRestaurants(restaurantDataCopy);
      } else {
        restaurantDataCopy = [...restaurants];
        restaurantDataCopy.sort((a, b) => {
          return b.info.avgRating - a.info.avgRating;
        });
        setRestaurants(restaurantDataCopy);
      }
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="restaurants">
          <img src={prevarrow} alt="arrow" />
          <p className="popularhead">Our Popular Restaurants</p>
          <img src={nextarrow} alt="arrow" />
        </div>
        <div className="container-fluid">
          <div className="row">
            <div className="col-2 pt-4">
              <input placeholder="Search Locations" />
              <br />
              <br />
              <label>
                <input
                  type="radio"
                  onChange={() => {
                    setLocation({
                      lat: '17.4485835',
                      long: '78.39080349999999'
                    });
                  }}
                  name="location"
                />{' '}
                Madhapur
              </label>
              <br />
              <label>
                <input
                  type="radio"
                  onChange={() => {
                    setLocation({
                      lat: '17.492838',
                      long: '78.54690149999999'
                    });
                  }}
                  name="location"
                />{' '}
                Sainikpuri
              </label>
            </div>
            <div className="col-10">
              <div style={{ textAlign: 'center', margin: '20px 0px' }}>
                <input
                  value={userSearch}
                  onChange={(e) => {
                    setUserSearch(e.target.value);
                  }}
                  style={{ width: '300px' }}
                  placeholder="search restaurents by name"
                />
                <div style={{ float: 'right', display: 'inline' }}>
                  <select name="filters" id="filters" onChange={(e) => sortByCategory(e.target.value)}>
                    <option value="delivery">Fast Delivery</option>
                    <option value="rating">Rating High to Low</option>
                  </select>
                </div>
              </div>

              <div className="row row-cols-1 row-cols-md-3 g-4 mt-2 mb-2">
                {filteredRestaurants.length > 0 ? (
                  filteredRestaurants.map((item, index) => (
                    <div className="col">
                      <div className="card">
                        <img
                          src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_660/${item.info.cloudinaryImageId}`}
                          className="card-img-top restaurentImage"
                          alt="..."
                        />
                        <div className="card-body">
                          <h5 className="card-title">{item.info.name}</h5>
                          <p className="card-text">
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="16"
                              height="16"
                              fill="currentColor"
                              className="bi bi-star-fill"
                              viewBox="0 0 16 16"
                            >
                              <path
                                d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z"
                              />
                            </svg>
                            <span style={{ marginLeft: '5px' }}>
                              {item.info.avgRating ? item.info.avgRating : item.info.avgRatingString}
                            </span>
                            <span style={{ marginLeft: '25px' }}>
                              <i className="fa-solid fa-motorcycle"></i>
                              {item.info.sla.slaString}
                            </span>
                          </p>
                          <p style={{ marginBottom: '0px' }} className="card-text">
                            {item.info.cuisines.slice(0, 3).join(' | ')}
                          </p>
                          <p style={{ marginTop: '5px' }} className="card-text">
                            {item.info.areaName}
                          </p>
                        </div>
                      </div>
                    </div>
                  ))
                ) : restaurants.length > 0 ? (
                  restaurants.map((item, index) => (
                    <div className="col">
                      {/* Render shimmer effect */}
                      <Shimmer />
                    </div>
                  ))
                ) : (
                  <p>No restaurants found.</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
