import React from 'react'
import './cart.css';
import { useSelector, useDispatch } from 'react-redux'
import { removeFromCart } from './reducer'

function Cart() {
    const dispatch = useDispatch()

    const cartItems = useSelector((state) => {
        return state.cartItems
    })

    // Calculate total price and quantity for all items in the cart
    const totalPrice = cartItems.reduce((acc, item) => {
        return acc + (item.Price * item.quantity); // Multiply price by quantity
    }, 0)

    return (
        <div>
            <h2 style={{ textAlign: "center" }}>Cart</h2>
            <h4 style={{ textAlign: "center" }}>Total Price: &#8377;{totalPrice}</h4>
            <div className="container">
                <div className="row row-cols-1 row-cols-md-2 g-2 m-4">
                    {cartItems.map((item, index) => (
                        <div key={index} className="row g-0">
                            <div className="col-md-6 p-4">
                                <img src={item.Img} className="img-fluid rounded" style={{ height: "250px", width: "300px" }} alt="..." />
                            </div>
                            <div className="col-md-6">
                                <div className="card-body">
                                    <h5 className="card-title">{item.Name}</h5>
                                    <p className="card-text"><b>&#8377;</b>{" " + (item.Price * item.quantity)}</p> {/* Display price multiplied by quantity */}
                                    <p className="card-text">{item.description}</p>
                                    <p className="card-text">Quantity: {item.quantity}</p> {/* Display quantity */}
                                    <button className='btn btn-danger'
                                        onClick={() => {
                                            dispatch(removeFromCart(index))
                                        }}
                                    >Remove From Cart</button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default Cart
