// Location.js
import React from 'react';

const Location = ({ showModal, handleModalClose, setLocation }) => {
  const locations = [
    { name: "Madhapur", lat: "17.4375084", long: "78.4482441" },
    { name: "Sainikpuri", lat: "17.492838", long: "78.54690149999999" },
    { name: "Gachibowli", lat: "17.4448", long: "78.3498" },
    { name: "Kondapur", lat: "17.4728", long: "78.3631" },
  ];

  const handleChangeLocation = (lat, long) => {
    setLocation({ lat, long });
    handleModalClose();
  };

  return (
    <div>
      {showModal && (
        <div className="modal" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Choose Location</h5>
                <button type="button" className="btn-close" aria-label="Close" onClick={handleModalClose}></button>
              </div>
              <div className="modal-body" style={{ maxHeight: "150px", overflowY: "scroll" }}>
                <form>
                  <div className="mb-3">
                    <label className="form-label">Select Location</label>
                      <div className="form-check" key={index}>
                        <input
                          className="form-check-input"
                          type="radio"
                          name="location"
                          onChange={() => {console.log(`Lat : 17.4485835 , Lng :78.39080349999999 `)}}
                        />
                        <label className="form-check-label">
                          Sainikpuri
                        </label>
                        <input
                          className="form-check-input"
                          type="radio"
                          name="location"
                          onChange={() => {console.log(`Lat : 17.492838 , Lng :78.54690149999999 `)}}
                        />
                        
                      </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Location;
