import React from 'react';
import './cart.css';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, updateQuantity } from './reducer';

function Cart() {
    const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.cartItems);

    // Calculate total price considering quantity
    let totalPrice = cartItems.reduce((acc, item) => {
        return acc + item.Price * item.Quantity;
    }, 0);

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Cart</h2>
            <h4 style={{ textAlign: 'center' }}>Total Price = &#8377;{totalPrice}</h4>
            <div className="container">
                <div className="row row-cols-1 row-cols-md-2 g-2 m-3 ">
                    {cartItems.map((item, i) => (
                        <div className="row g-0 p-4" key={i} id = "cartrow" >
                            <div className="col-md-6 p-4">
                                <img
                                    src={item.Img}
                                    className="img-fluid rounded"
                                    style={{ height: '250px', width: '300px' }}
                                    alt="..."
                                />
                            </div>
                            <div className="col-md-6">
                                <div className="card-body">
                                    <h5 className="card-title">{item.Name}</h5>
                                    <p className="card-text">
                                        <b>&#8377;</b> {item.Price} x {item.Quantity} = &#8377;{item.Price * item.Quantity}
                                    </p>
                                    <p className="card-text">{item.Description}</p>
                                    <div className="d-flex">
                                        <button
                                            className="btn btn-danger ms-2"
                                            id = "removecart"
                                            onClick={() => {
                                                // Prompt user to specify quantity to remove
                                                const quantityToRemove = parseInt(prompt(`Enter quantity to remove for ${item.Name}:`, item.Quantity));
                                                if (!isNaN(quantityToRemove) && quantityToRemove > 0) {
                                                    dispatch(removeFromCart({ index: i, quantity: quantityToRemove }));
                                                }
                                            }}
                                        >
                                            Remove From Cart
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Cart;
