import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../components/restaurantspec.css';
import { addToCart } from './reducer';
import { useDispatch } from 'react-redux';

function Restaurantspec() {
    const dispatch = useDispatch();
    const { restaurantId, restaurantName } = useParams();
    const [restaurantMenu, setRestaurantMenu] = useState([]);
    const [openAccordion, setOpenAccordion] = useState(0);

    useEffect(() => {
        axios.get(`https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=17.37240&lng=78.43780&restaurantId=${restaurantId}&catalog_qa=undefined&submitAction=ENTER`)
            .then((res) => {
                const menuData = res.data?.data?.cards[4]?.groupedCard?.cardGroupMap?.REGULAR?.cards.slice(1);
                setRestaurantMenu(menuData || []);
            })
            .catch((error) => {
                console.error('Error fetching restaurant menu:', error);
                setRestaurantMenu([]); // Set empty array in case of error to prevent undefined mapping
            });
    }, [restaurantId]);

    const toggleAccordion = (index) => {
        setOpenAccordion(openAccordion === index ? null : index); // Toggle the open state of the accordion
    };

    const handleAddToCart = (item, quantity) => {
        if (!item || !item.card || !item.card.info) {
            console.error('Invalid item data:', item);
            return;
        }
        
        const price = item.card.info.price ? item.card.info.price / 100 : item.card.info.defaultPrice / 100;
        if (isNaN(price)) {
            console.error('Invalid price data:', item.card.info.price, item.card.info.defaultPrice);
            return;
        }
        
        dispatch(addToCart({
            Name: item.card.info.name,
            Description: item.card.info.description,
            Price: price,
            Img: `https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_600/${item.card.info.imageId}`,
            Quantity: quantity
        }));
    };

    return (
        <div className="container">
            <h2 className='restaurant-title'>{restaurantName} Menu</h2>
            <div className="accordion" id="accordionExample">
                {restaurantMenu.map((group, index) => {
                    if (group?.card?.card?.itemCards) {
                        return (
                            <div className="accordion-item" key={index}>
                                <h2 className="accordion-header">
                                    <button
                                        className={`accordion-button ${openAccordion === index ? '' : 'collapsed'}`}
                                        type="button"
                                        onClick={() => toggleAccordion(index)}
                                        aria-expanded={openAccordion === index ? 'true' : 'false'}
                                        aria-controls={`collapse${index}`}
                                        data-bs-toggle="collapse"
                                        data-bs-target={`#collapse${index}`}
                                    >
                                        <b className='category'>{group.card.card.title} - {group.card.card.itemCards.length}</b>
                                        <i className={`fas ${openAccordion === index ? 'fa-chevron-up' : 'fa-chevron-down'} accordion-icon`}></i>
                                    </button>
                                </h2>
                                <div
                                    id={`collapse${index}`}
                                    className={`accordion-collapse collapse ${openAccordion === index ? 'show' : ''}`}
                                    aria-labelledby={`heading${index}`}
                                    data-bs-parent="#accordionExample"
                                >
                                    <div className="accordion-body">
                                        <div className="row row-cols-1 row-cols-md-6 g-2">
                                            {group.card.card.itemCards.map((item, subIndex) => (
                                                <div className="col" key={subIndex}>
                                                    <div className="card">
                                                        <img
                                                            src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_300,h_300,c_fit/${item.card.info.imageId}`}
                                                            className="card-img-top food-pic"
                                                            alt={item.card.info.name}
                                                        />
                                                        <div className="card-body">
                                                            <h5 className="card-title">{item.card.info.name}</h5>
                                                            {item.card.info.ratings?.aggregatedRating?.rating && (
                                                                <p>
                                                                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                                                                        <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                                                                    </svg>
                                                                    {" " + item.card.info.ratings.aggregatedRating.rating}
                                                                    <span style={{ marginLeft: "10px" }}>{item.card.info.ratings.aggregatedRating.ratingCount}</span>
                                                                </p>
                                                            )}
                                                            <p className="card-text">
                                                                <b>Price : </b> {
                                                                    item.card.info.price
                                                                        ? (Number(item.card.info.price) / 100).toFixed(2)
                                                                        : item.card.info.defaultPrice
                                                                            ? (Number(item.card.info.defaultPrice) / 100).toFixed(2)
                                                                            : 'N/A'
                                                                }
                                                            </p>
                                                            <div className="d-flex align-items-center">
                                                                <input
                                                                    type="number"
                                                                    min="1"
                                                                    defaultValue="1"
                                                                    onChange={(e) => {
                                                                        const quantity = parseInt(e.target.value);
                                                                        if (!isNaN(quantity)) {
                                                                            handleAddToCart(item, quantity);
                                                                        }
                                                                    }}
                                                                />
                                                                <button
                                                                    className="btn btn-primary ms-2"
                                                                    onClick={() => handleAddToCart(item, 1)}
                                                                >
                                                                    Add to Cart
                                                                </button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        );
                    }
                    return null;
                })}
            </div>
        </div>
    );
}

export default Restaurantspec;
