import React from 'react'
import { useSelector , useDispatch } from 'react-redux'
import {removeFromCart} from './reducer'

function Cart() {

    let dispatch = useDispatch()

    let cartItems = useSelector((state) => {
        return state.cartItems
    })

    console.log(cartItems)

    let totalPrice = cartItems.reduce((acc,item ,i)=>{
        return acc + item.Price
    },0)

    return (
        <div>
            <h2 style={{ textAlign: "center" }}>Cart</h2>
            <h4 style={{ textAlign: "center" }}>Price = {totalPrice}</h4>
            <div class="container">
                <div class="row row-cols-1 row-cols-md-2 g-2 m-4">
                    {cartItems.map((item ,i)=>{
                        return <div class="row g-0">
                            <div class="col-md-6 p-4">
                                <img src= {item.Img} class="img-fluid rounded" style = {{height:"200px" ,width : "300px"}} alt="..." />
                            </div>
                            <div class="col-md-6">
                                <div class="card-body">
                                    <h5 class="card-title">{item.Name}</h5>
                                    <p class="card-text">{item.Price}</p>
                                    <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                                    <button className='btn btn-danger'
                                    onClick = {()=>{
                                        dispatch(removeFromCart(i))
                                    }}
                                    >Remove From Cart</button>
                                </div>
                            </div>
                        </div>
                    })}
                </div>
            </div>
        </div>)}

export default Cart
