import React, { useState } from 'react';
import prevarrow from '../images/prev-arrow.png';
import nextarrow from '../images/white-next-arrow.png';

export default function Header() {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <div id="header" className="d-flex align-items-center justify-content-between">
      <img src={prevarrow} alt="arrow" className="arrow" />
      <div className="d-none d-lg-flex">
        <a>Brand</a>
        <a>Search Dishes</a>
        <a>Search Restaurants</a>
        <a><i className="fa-solid fa-cart-shopping"></i> Cart</a>
      </div>
      <img src={nextarrow} alt="arrow" className="d-none d-lg-inline" />
      <div className="d-lg-none">
        <button className="navbar-toggler" type="button" onClick={toggleMenu}>
          <span className="navbar-toggler-icon"></span>
        </button>
      </div>
      {showMenu && (
        <div className="d-lg-none">
          <div className="menu">
            <a>Brand</a>
            <a>Search Dishes</a>
            <a>Search Restaurants</a>
            <a><i className="fa-solid fa-cart-shopping"></i> Cart</a>
            <button onClick={toggleMenu}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}
