import React, { useState } from 'react';
import Header from '../src/components/header';
import { FilterProvider } from './components/filtercontext';
import Home from '../src/components/home'; // Import Home component

const App = () => {
  const [userSearch, setUserSearch] = useState("");
  const [showModal, setShowModal] = useState(false); // State for modal

  return (
    <FilterProvider>
      <Header setUserSearch={setUserSearch} setShowModal={setShowModal} />
      <Home userSearch={userSearch} setUserSearch={setUserSearch} showModal={showModal} setShowModal={setShowModal} />
    </FilterProvider>
  );
}

export default App;
