import React, { useState } from 'react';
import './cart.css';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from './reducer';

function Cart() {
    const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.cartItems);

    // State to store the quantity to remove for each item
    const [removeQuantities, setRemoveQuantities] = useState({});

    // Consolidate items in the cart and calculate total price
    const cartItemsMap = {};
    cartItems.forEach((item) => {
        if (cartItemsMap[item.id]) {
            cartItemsMap[item.id].quantity += 1;
        } else {
            cartItemsMap[item.id] = { ...item, quantity: 1 };
        }
    });

    // Calculate total price
    const totalPrice = Object.values(cartItemsMap).reduce((acc, item) => {
        return acc + item.Price * item.quantity;
    }, 0);

    // Handle change in quantity to remove
    const handleChange = (itemId, event) => {
        const { value } = event.target;
        setRemoveQuantities((prev) => ({ ...prev, [itemId]: parseInt(value) || 0 }));
    };

    // Remove specified quantity of an item from the cart
    const handleRemove = (itemId) => {
        dispatch(removeFromCart({ itemId, quantity: removeQuantities[itemId] || 0 }));
        setRemoveQuantities((prev) => ({ ...prev, [itemId]: 0 })); // Reset quantity to remove
    };

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Cart</h2>
            <h4 style={{ textAlign: 'center' }}>Total Price = &#8377;{totalPrice}</h4>
            <div className="container">
                <div className="row row-cols-1 row-cols-md-2 g-2 m-4">
                    {Object.values(cartItemsMap).map((item, index) => (
                        <div key={index} className="row g-0">
                            <div className="col-md-6 p-4">
                                <img src={item.Img} className="img-fluid rounded" style={{ height: '250px', width: '300px' }} alt="..." />
                            </div>
                            <div className="col-md-6">
                                <div className="card-body">
                                    <h5 className="card-title">{item.Name}</h5>
                                    <p className="card-text">&#8377; {item.Price}</p>
                                    <p className="card-text">{item.description}</p>
                                    <p className="card-text">Quantity: {item.quantity}</p>
                                    <div className="form-group">
                                        <label htmlFor={`remove-${item.id}`}>Remove Quantity:</label>
                                        <input type="number" className="form-control" id={`remove-${item.id}`} value={removeQuantities[item.id] || 0} onChange={(e) => handleChange(item.id, e)} />
                                    </div><br/>
                                    <button className='btn btn-danger' onClick={() => handleRemove(item.id)}>Remove From Cart</button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Cart;
