// Cart.js
import React from 'react';
import './cart.css';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, clearCart } from './reducer';
import ScrollToTopButton from './scrolltop';

function Cart() {
    const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.cartItems);

    // Calculate total price considering quantity
    let totalPrice = cartItems.reduce((acc, item) => {
        const price = parseFloat(item.Price); // Ensure Price is parsed as float
        const quantity = parseInt(item.Quantity); // Ensure Quantity is parsed as integer
        return acc + (price * quantity); // Accumulate total price
    }, 0);

    let discount = 0; // No discount

    if (totalPrice > 1000) {
        discount = totalPrice * 0.1; // 10% discount
    }

    const totalAmount = totalPrice - discount; // Calculate total amount after discount

    // Handle clear cart and place order logic
    const handleClearCart = () => {
        if (cartItems.length === 0) {
            alert('Please purchase items to place an order');
        } else {
            dispatch(clearCart());
            alert('Are you sure you want to clear cart');
        }
    };

    return (
        <>
            <div className="cart-container">
                <div className="cart-header">
                    <h2>Shopping Cart</h2>
                    <div>
                        <p>Number of items: {cartItems.length}</p>
                        <button
                            className="cart-remove-btn"
                            style={{ width: "200px" }}
                            id="removecart"
                            onClick={handleClearCart} // Call handleClearCart to clear the cart
                        >
                            Clear Cart
                        </button>
                    </div>
                </div>
                <div className="cart-items">
                    {cartItems.map((item, i) => (
                        <div className="cart-item" key={i}>
                            <h1>Restaurant: {item.RestaurantName}</h1>
                            <img src={item.Img} alt={item.Name} />
                            <div className="cart-item-details">
                                <h5>{item.Name}</h5>
                                <p>{item.Description}</p>
                            </div>
                            <div className="cart-item-price">
                                <b>&#8377;</b> {parseFloat(item.Price).toFixed(2)}
                            </div>
                            <div className="cart-item-quantity">
                                <label>Quantity: {item.Quantity}</label>
                            </div>
                            <div className="cart-item-total">
                                Total: <b>&#8377;</b> {(parseFloat(item.Price) * parseInt(item.Quantity)).toFixed(2)}
                            </div>
                            <button
                                className="removecart"
                                id={`removecart-${i}`}
                                onClick={() => {
                                    const quantityToRemove = parseInt(prompt(`Enter quantity to remove for ${item.Name}:`, item.Quantity));
                                    if (!isNaN(quantityToRemove) && quantityToRemove > 0) {
                                        dispatch(removeFromCart({ index: i, quantity: quantityToRemove }));
                                    }
                                }}
                            >
                                Remove From Cart
                            </button>
                        </div>
                    ))}
                </div>
                <div className="cart-summary">
                    <h4>Order Summary</h4>
                    <div className="cart-summary-item">
                        Total Price: <b>&#8377;</b> {totalPrice.toFixed(2)}
                    </div>
                    <div className="cart-summary-item">
                        Discount: <b>&#8377;</b> {discount.toFixed(2)}<br />
                        <em style={{ color: "red" }}>* (To avail the discount, the purchase price must exceed 999)</em>
                    </div>
                    <div className="cart-summary-item">
                        Total Amount: <b>&#8377;</b> {totalAmount.toFixed(2)}
                    </div>
                    <button className="order" onClick={handleClearCart}>
                        Place Order
                    </button>
                </div>
            </div>
            <ScrollToTopButton />
        </>
    );
}

export default Cart;
