import { createSlice } from "@reduxjs/toolkit";

let initialState = {
    cartItems: []
};

let slice = createSlice({
    name: "cart",
    initialState: initialState,
    reducers: {
        addToCart: (state, action) => {
            const { id } = action.payload;
            const existingItem = state.cartItems.find(item => item.id === id);
            if (existingItem) {
                existingItem.quantity += 1; // Increase quantity if item already exists
            } else {
                state.cartItems.push({ ...action.payload, quantity: 1 }); // Add as new item with quantity 1
            }
        },
        removeFromCart: (state, action) => {
            const indexToRemove = state.cartItems.findIndex(item => item.id === action.payload);
            if (indexToRemove !== -1) {
                state.cartItems.splice(indexToRemove, 1);
            }
        },
        updateQuantity: (state, action) => {
            const { itemId, quantity } = action.payload;
            const itemToUpdate = state.cartItems.find(item => item.id === itemId);
            if (itemToUpdate) {
                itemToUpdate.quantity = quantity;
            }
        }
    }
});

export const { addToCart, removeFromCart, updateQuantity } = slice.actions;
export default slice.reducer;
