import React , { useEffect , useState } from 'react';
import axios from 'axios';
import styles from './App.css';
import Header from './components/header';
import { FilterProvider } from './filtercontext'; // Import FilterProvider

function App() {
  let [restaurants, setRestaurants] = useState([]);
  let [userSearch, setUserSearch] = useState("");
  let [filteredRestaurants, setFilteredRestaurants] = useState([]);

  useEffect(() => {
    axios.get(`https://www.swiggy.com/dapi/restaurants/list/v5?lat=17.37240&lng=78.43780&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`)
      .then((res) => {
        setRestaurants(res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
      });
  }, []);

  useEffect(() => {
    let result = restaurants.filter((item) => {
      if(item.info.name.toLowerCase().includes(userSearch.toLowerCase())) {
        return true;
      }
    });
    setFilteredRestaurants(result);
  }, [userSearch]);

  return (
    <FilterProvider> {/* Wrap entire application with FilterProvider */}
      <div className="container-fluid">
        <Header userSearch={userSearch} setUserSearch={setUserSearch}/>
        <FilteredContent restaurants={filteredRestaurants} /> {/* Pass filteredRestaurants to FilteredContent */}
      </div>
    </FilterProvider>
  );
}

// Create a new component for the filtered content
const FilteredContent = ({ restaurants }) => {
  return (
    <div className="row">
      <div className="col-12">
        <div className="row row-cols-1 row-cols-md-4 g-4">
          {restaurants.map((item, i) => (
            <div className="col" key={i}>
              {/* Render filtered restaurant items */}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
