import React , {useState , useEffect} from 'react'
import axios from 'axios'

function SearchDishes() {

  const [dishName , setDishName] = useState("")
  const [dishes , setDishes] = useState([])

  useEffect(()=>{
    if(dishName.length > 2)
    {
      axios.get(`https://www.swiggy.com/dapi/restaurants/search/v3?lat=17.372040&lng=78.43780
      &str=${dishName}&trackingId=undefined&submitAction=SUGGESTION&queryUniqueId=e8f976e5-270e-3d4d-aca0-573a2513c73a`)
      .then((res)=>{
        if(res.data.data?.cards[1]?.groupedCard?.cardGroupMap?.DISH){
          setDishes(res.data.data?.cards[1]?.groupedCard?.cardGroupMap.DISH.cards.slice(1))
        }
    })
  }
  },[dishName])

  console.log(dishes)

  return (
    <div>
      <div style={{textAlign : "center"}}>
        <h2>SearchDishes</h2>
        <input type = "text" value = {dishName}
        onChange = {(e)=>{
          setDishName(e.target.value)
        }}
        placeholder='Search the dishes'/>
      </div>
      <div class="row row-cols-1 row-cols-md-3 g-4">
        <div class="col">
          <div class="card h-100">
            <h3 class="card-title">Restaurant Name</h3>
            <p>Rating , Time to deliver <button>View Restaurant</button></p>
            
            <div className='row'>
              <div className = "col-4">
            <img src="..." class="card-img-top" alt="..."/>
            </div>
            <div class="card-body col-8">
              <h5 class="card-title">Card title</h5>
              <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
            </div>
            </div>
          
          </div>
        </div>
      </div>
    </div>
  )
}

export default SearchDishes
