import React, { useState } from 'react';

export default function Header() {
  const [toggleMenu, setToggleMenu] = useState(false);

  const handleToggle = () => {
    setToggleMenu(!toggleMenu);
  };

  return (
    <div id="header" className="container">
      <div className="row align-items-center">
        <div className="col-md-6">
          <button className="btn btn-light d-md-none" onClick={handleToggle}>
            <i className={`fa ${toggleMenu ? 'fa-times' : 'fa-bars'}`}></i>
          </button>
          <span className="d-none d-md-inline">Brand</span>
        </div>
        <div className={`col-md-6 ${toggleMenu ? 'd-block' : 'd-none'}`}>
          <div className="menu">
            <a>Search Dishes</a>
            <a>Search Restaurants</a>
            <a>
              <i className="fa-solid fa-cart-shopping"></i> Cart
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}
