// https://www.swiggy.com/dapi/restaurants/search/v3?lat=17.37240&lng=78.43780&str=${restaurantName}&trackingId=undefined&submitAction=SUGGESTION&queryUniqueId=3445c27e-9767-0109-930c-a7c5b2183e33
import React , {useState , useEffect} from 'react'
import axios from 'axios'

export default function SearchRestaurants() {

  const [restaurantName , setRestaurantName] = useState("")

  useEffect(()=>{
    axios.get(`https://www.swiggy.com/dapi/restaurants/search/v3?lat=17.37240&lng=78.43780&str=${restaurantName}&trackingId=undefined&submitAction=SUGGESTION&queryUniqueId=3445c27e-9767-0109-930c-a7c5b2183e33`)
    .then((res)=>{
      console.log(res.data)
    })
  },[restaurantName])
  
  return (
    <div style={{textAlign : "center"}}>
      <h1>Search Restaurants</h1>
      <input type = "text"
      value = {restaurantName}
      onChange={(e)=>{
        setRestaurantName(e.target.value)
      }}
      placeholder='search restaurants'/>
    </div>
  )
}
