import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { clearCart } from './reducer';
import './printbill.css'; // Create a separate CSS file for styling

const PrintBill = () => {
    const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.cartItems);

    const totalPrice = cartItems.reduce((acc, item) => {
        const price = parseFloat(item.Price);
        const quantity = parseInt(item.Quantity);
        return acc + (price * quantity);
    }, 0);

    const discount = totalPrice > 999 ? totalPrice * 0.1 : 0;
    const totalAmount = totalPrice - discount;

    const handlePrint = () => {
        window.print();
        dispatch(clearCart());
        alert('Order placed and cart cleared successfully');
    };

    return (
        <div className="print-bill-container">
            <h2>Order Summary</h2>
            <div className="bill-items">
                {cartItems.map((item, i) => (
                    <div key={i} className="bill-item">
                        <h3>{item.Name}</h3>
                        <p>Quantity: {item.Quantity}</p>
                        <p>Price: &#8377; {parseFloat(item.Price).toFixed(2)}</p>
                        <p>Total: &#8377; {(parseFloat(item.Price) * parseInt(item.Quantity)).toFixed(2)}</p>
                    </div>
                ))}
            </div>
            <div className="bill-summary">
                <p>Total Price: &#8377; {totalPrice.toFixed(2)}</p>
                {totalPrice > 999 && <p>Discount: &#8377; {discount.toFixed(2)}</p>}
                <p>Total Amount: &#8377; {totalAmount.toFixed(2)}</p>
            </div>
            <button className="print-bill-button" onClick={handlePrint}>Print Bill</button>
        </div>
    );
};

export default PrintBill;
