import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Shimmer from './shimmer'; // Placeholder for loading animation
import { useNavigate } from 'react-router-dom';
import ScrollToTopButton from './scrolltop';

export default function SearchRestaurants() {
  const [restaurantName, setRestaurantName] = useState('');
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  let navigate = useNavigate();

  useEffect(() => {
    let cancelRequest = false;
    if (restaurantName) {
      setLoading(true); // Set loading to true before making the API call
      axios
        .get(`https://www.swiggy.com/dapi/restaurants/search/v3?lat=17.37240&lng=78.43780&str=${restaurantName}&trackingId=undefined&submitAction=SUGGESTION&queryUniqueId=3445c27e-9767-0109-930c-a7c5b2183e33`)
        .then((response) => {
          if (!cancelRequest) {
            setRestaurants(response.data.data?.cards[1]?.groupedCard?.cardGroupMap?.RESTAURANT || []);
            setLoading(false); // Set loading to false after API call completes
            setError('');
          }
        })
        .catch((error) => {
          if (!cancelRequest) {
            setError('Error fetching restaurants. Please try again later.'); // Set error message
            setLoading(false); // Set loading to false on error
            setRestaurants([]); // Clear restaurants on error
          }
        });

      return () => {
        cancelRequest = true; // Set cancelRequest to true on component unmount
      };
    } else {
      setRestaurants([]); // Clear restaurants if restaurantName is empty
    }
  }, [restaurantName]);

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Search Restaurants</h1>
      <form className="d-flex justify-content-center" role="search">
        <div className="col-md-6 col-lg-4 m-2 clearable-input">
          <input
            className="form-control"
            value={restaurantName}
            onChange={(e) => {
              setRestaurantName(e.target.value);
              setError(''); // Clear error message when input changes
            }}
            type="search"
            placeholder="Search Restaurants By Name"
            aria-label="Search"
          />
        </div>
      </form>
      {loading && <p style={{ marginTop: '10px', color: '#888' }}>Loading...</p>}
      {error && <p style={{ marginTop: '10px', color: 'red' }}>{error}</p>}
      <div class="container">
        <div class="row row-cols-1 row-cols-md-4 g-4">
          {restaurants?.cards?.map((item, i) => {
            if (item?.card?.card?.info) {
              return (
                <div className="col" key={i}>
                  <div className="card h-100">
                    <img className="card-img-top" src={`https://media-assets.swiggy.com/swiggy/image/upload/f_auto,q_auto,w_660/${item?.card?.card?.info?.cloudinaryImageId}`} alt="Restaurant" />
                    <div className="card-body">
                      <h5 className="card-title">{item?.card?.card?.info?.name}</h5>
                      <p className="card-text">
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                          <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                        </svg>
