import React, { useState } from 'react';
import styles from '../components/header.css';
import { useFilters } from '../../src/components/filtercontext';

const Header = ({ userSearch, setUserSearch }) => {
  const [searchBarVisible, setSearchBarVisible] = useState(false);
  const { setFilters } = useFilters();

  const handleSearchLinkClick = () => {
    setSearchBarVisible(!searchBarVisible);
  };

  const handleFilterClick = (filter) => {
    setFilters(prevFilters => {
      if (!prevFilters.includes(filter)) {
        return [...prevFilters, filter];
      }
      return prevFilters;
    });
  };
  

  const handleLocationClick = (location) => {
    // Handle location selection
    setSearchBarVisible(false); // Hide search bar after location selection
  };

  return (
    <nav className={`navbar navbar-expand-lg ${styles['bg-body-tertiary']}`}>
      <div className="container-fluid">
        <a className="navbar-brand" href="#">
          <h1 className={`text-primary m-0`} id="headtitle">
            <i className="fa fa-utensils me-3"></i>Restoran
          </h1>
        </a>
        <button className="navbar-toggler" type="button" aria-label="Toggle navigation" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className={`collapse navbar-collapse ${searchBarVisible ? 'show' : ''}`} id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0 justify-content-center w-100">
            <li className="nav-item">
              <a className="nav-link active" aria-current="page" href="#">Home</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#">Link</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={handleSearchLinkClick}>Search Restaurants</a>
            </li>
          </ul>
          {searchBarVisible && (
            <form className="d-flex position-absolute start-0 top-100 w-100 p-3 bg-light border" role="search">
              <input
                className="form-control me-2"
                value={userSearch}
                onChange={(e) => setUserSearch(e.target.value)}
                type="search"
                placeholder="Search Locations"
                aria-label="Search"
              />
              <button className="btn btn-outline-success" id="searchbtn" type="submit">
                Search
              </button>
              <ul className="list-group">
                <li className="list-group-item" onClick={() => handleLocationClick('Location 1')}>Location 1</li>
                <li className="list-group-item" onClick={() => handleLocationClick('Location 2')}>Location 2</li>
                <li className="list-group-item" onClick={() => handleLocationClick('Location 3')}>Location 3</li>
              </ul>
              <small className="text-muted mt-2">* Click anywhere outside to close</small>
            </form>
          )}
          <div className="dropdown">
            <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
              Filters
            </button>
            <ul className="dropdown-menu">
              <li>
                <a className="dropdown-item" onClick={() => handleFilterClick('fastDelivery')} href="#">Fast Delivery</a>
              </li>
              <li>
                <a className="dropdown-item" onClick={() => handleFilterClick('ratinghightolow')} href="#">Rating : High to Low</a>
              </li>
              <li>
                <a className="dropdown-item" onClick={() => handleFilterClick('ratinglowtohigh')} href="#">Rating : Low to High</a>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </nav>
  );
}

export default Header;
