import { createSlice } from "@reduxjs/toolkit";


let initialState = {
    cartItems : []
}

let slice = createSlice({
    name : "cart",
    initialState : initialState,
    reducers :{
        addToCart : (state , action)=>{
            console.log(state);
        },

        removeFromCart : (state , action)=>{

        }
    }
})

export const {addToCart} = slice.actions
export default slice.reducer;
