import React, { createContext, useContext, useState , useEffect } from 'react';

const FilterContext = createContext();

export const FilterProvider = ({ children }) => {
  const [filters, setFilters] = useState([]);
  const [userSearch , setUserSearch] = useState("")


  useEffect(()=>{
    let result = restaurants.filter((item , i)=>{
      if(item.info.name.includes("userSearch") == true)
        {
          return true
        }
    })
  },[userSearch])

  console.log(result)
  
  const applyFilters = (restaurants) => {
    let filtered = [...restaurants];

    if (filters.includes('fastDelivery')) {
      filtered.sort((a, b) => {
        const timeA = parseInt(a.info.sla.slaString.split(' ')[0]);
        const timeB = parseInt(b.info.sla.slaString.split(' ')[0]);
        return timeA - timeB;
      });
    }

    return filtered;
  };

  return (
    <FilterContext.Provider value={{ filters, setFilters, applyFilters }}>
      {children}
    </FilterContext.Provider>
  );
};

export const useFilters = () => {
  return useContext(FilterContext);
};
