import axios from 'axios';
import React, { useState, useEffect } from 'react';

function Locations({ setLocations }) {
  let [userLocationSearch, setUserLocationSearch] = useState("");
  let [locationSuggestion, setLocationSuggestion] = useState([]);

  useEffect(() => {
    if (userLocationSearch) {
      axios.get(`https://www.swiggy.com/dapi/misc/place-autocomplete?input=${userLocationSearch}&types=`)
        .then((res) => {
          if (res.data.data) {
            setLocationSuggestion(res.data.data);
          }
        });
    }
  }, [userLocationSearch]);

  const clearInput = () => {
    setUserLocationSearch("");
    setLocationSuggestion([]);
  };

  const highlightStyle = {
    border: '1px solid rgba(255, 69, 0, 0.5)!important', // OrangeRed with 50% opacity for the border
  };

  return (
    <div className="col-2 pt-4">
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={userLocationSearch}
          onChange={(e) => {
            setUserLocationSearch(e.target.value);
          }}
          placeholder="Search Locations"
          style={userLocationSearch ? {...highlightStyle, border: '1px solid rgba(255, 69, 0, 0.5) !important'} : {}}
        />
        <button className="btn btn-outline-secondary" type="button" onClick={clearInput}>
          <i className="fa fa-times"></i>
        </button>
      </div>
      <ol style={{ textAlign: "left", padding: "7px 20px" }}>
        {userLocationSearch !== "" ? locationSuggestion?.map((item, i) => {
          return (
            <li
              onClick={() => {
                axios.get(`https://www.swiggy.com/dapi/misc/address-recommend?place_id=${item.place_id}`)
                  .then((res) => {
                    let locationData = res.data.data[0].geometry.location;
                    setLocations({
                      lat: locationData.lat,
                      long: locationData.lng
                    });
                    setLocationSuggestion([]);
                  });
              }}
              key={i}
              className="locationName"
            >
              {item.description}
            </li>
          );
        }) : ""}
      </ol>
    </div>
  );
}

export default Locations;
