import React from 'react';
import './cart.css';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from './reducer';

function Cart() {
    const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.cartItems);

    const handleRemove = (index) => {
        dispatch(removeFromCart(index));
    };

    // Calculate total price
    const totalPrice = cartItems.reduce((acc, item) => {
        return acc + item.Price * item.quantity;
    }, 0);

    const updateQuantity = (index, newQuantity) => {
        const updatedItems = [...cartItems];
        updatedItems[index].quantity = newQuantity;
        dispatch(updateCart(updatedItems));
    };

    const addToCart = (product) => {
        const index = cartItems.findIndex((item) => item.id === product.id);
        if (index !== -1) {
            updateQuantity(index, cartItems[index].quantity + 1);
        } else {
            dispatch(addToCart(product));
        }
    };

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Cart</h2>
            <h4 style={{ textAlign: 'center' }}>Total Price = &#8377;{totalPrice}</h4>
            <div className="container">
                <div className="row row-cols-1 row-cols-md-2 g-2 m-4">
                    {cartItems.map((item, index) => (
                        <div key={index} className="row g-0">
                            <div className="col-md-6 p-4">
                                <img src={item.Img} className="img-fluid rounded" style={{ height: '250px', width: '300px' }} alt="..." />
                            </div>
                            <div className="col-md-6">
                                <div className="card-body">
                                    <h5 className="card-title">{item.Name}</h5>
                                    <p className="card-text">&#8377; {item.Price}</p>
                                    <p className="card-text">Quantity: {item.quantity}</p>
                                    <button className='btn btn-danger' onClick={() => handleRemove(index)}>Remove From Cart</button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Cart;
