import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import prevarrow from '../images/prev-arrow.png';
import nextarrow from '../images/white-next-arrow.png';

export default function Header() {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [userSearch, setUserSearch] = useState('');

  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  return (
    <div id="header" className="navbar navbar-expand-lg navbar-dark bg-orangered">
      <div className="container-fluid">
        <img src={prevarrow} alt="arrow" className="arrow" />
        <Link to="/" className="navbar-brand d-none d-lg-block">Brand</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              {isSearchOpen ? (
                <input
                  value={userSearch}
                  onChange={(e) => setUserSearch(e.target.value)}
                  style={{ width: "300px" }}
                  placeholder="Search restaurants by name"
                />
              ) : (
                <button className="btn btn-link" onClick={toggleSearch} style={{ display: 'none' }}>Toggle Search</button>
              )}
            </li>
            <li className="nav-item">
              <a className="nav-link">Search Restaurants</a>
            </li>
            <li className="nav-item">
              <a className="nav-link"><i className="fa-solid fa-cart-shopping"></i> Cart</a>
            </li>
          </ul>
        </div>
        <img src={nextarrow} alt="arrow" />
      </div>
    </div>
  );
}
