import React, { useState } from 'react';
import './cart.css';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from './reducer';

function Cart() {
    const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.cartItems);

    // State to store the quantity for each unique item
    const [itemQuantities, setItemQuantities] = useState({});

    // Calculate total price
    const totalPrice = Object.keys(itemQuantities).reduce((acc, itemId) => {
        const item = cartItems.find((item) => item.id === itemId);
        return acc + (item ? item.Price * itemQuantities[itemId] : 0);
    }, 0);

    // Handle change in quantity for an item
    const handleChange = (itemId, event) => {
        const { value } = event.target;
        setItemQuantities((prev) => ({ ...prev, [itemId]: parseInt(value) || 0 }));
    };

    // Add an item to the cart or update its quantity if already present
    const addToCart = (item) => {
        if (itemQuantities[item.id]) {
            setItemQuantities((prev) => ({ ...prev, [item.id]: prev[item.id] + 1 }));
        } else {
            setItemQuantities((prev) => ({ ...prev, [item.id]: 1 }));
        }
    };

    // Remove specified quantity of an item from the cart
    const handleRemove = (itemId) => {
        dispatch(removeFromCart({ itemId, quantity: itemQuantities[itemId] || 0 }));
        setItemQuantities((prev) => ({ ...prev, [itemId]: 0 })); // Reset quantity to remove
    };

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Cart</h2>
            <h4 style={{ textAlign: 'center' }}>Total Price = &#8377;{totalPrice}</h4>
            <div className="container">
                {cartItems.map((item, index) => (
                    <div key={index} className="row g-0">
                        <div className="col-md-6 p-4">
                            <img src={item.Img} className="img-fluid rounded" style={{ height: '250px', width: '300px' }} alt="..." />
                        </div>
                        <div className="col-md-6">
                            <div className="card-body">
                                <h5 className="card-title">{item.Name}</h5>
                                <p className="card-text">&#8377; {item.Price}</p>
                                <p className="card-text">{item.description}</p>
                                <p className="card-text">Quantity: {itemQuantities[item.id] || 0}</p>
                                <div className="form-group">
                                    <button className='btn btn-success' onClick={() => addToCart(item)}>Add To Cart</button>
                                    <button className='btn btn-danger' onClick={() => handleRemove(item.id)}>Remove From Cart</button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Cart;
