import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/header';
import { FilterProvider, useFilters } from './components/filtercontext';
import { styles } from '../src/App.css';

const AppContent = ({ userSearch, setUserSearch }) => {
  const { filters, applyFilters } = useFilters();
  const [restaurants, setRestaurants] = useState([]);
  const [location, setLocation] = useState({
    lat: "17.4375084",
    long: "78.4482441"
  });

  const [locationModalOpen, setLocationModalOpen] = useState(false); // State for controlling the location modal

  useEffect(() => {
    axios.get(`https://www.swiggy.com/dapi/restaurants/list/v5?lat=${location.lat}&lng=${location.long}&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`)
      .then((res) => {
        const allRestaurants = res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants;
        setRestaurants(allRestaurants);
      });
  }, [location]);

  useEffect(() => {
    setRestaurants(prevRestaurants => applyFilters(prevRestaurants));
  }, [filters, applyFilters]);

  // Filter restaurants based on search value
  const filteredRestaurants = restaurants.filter(restaurant =>
    restaurant.info.name.toLowerCase().includes(userSearch.toLowerCase())
  );

  // Function to open the location modal
  const handleLocationModalOpen = () => {
    setLocationModalOpen(true);
  };

  // Function to close the location modal
  const handleLocationModalClose = () => {
    setLocationModalOpen(false);
  };

  return (
    <div className="container-fluid">
      <Header setUserSearch={setUserSearch} />

      {/* Location modal */}
      {locationModalOpen && (
        <div className="modal" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          {/* Add modal content here */}
        </div>
      )}

      <div className="row">
        <div className="col-12">
          <div className="row row-cols-1 row-cols-md-4 g-4">
            {/* Restaurant cards */}
            {filteredRestaurants.map((item, i) => (
              <div className="col" key={i}>
                <div className="card h-100">
                  {/* Card content */}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

const App = () => {
  const [userSearch, setUserSearch] = useState("");

  return (
    <FilterProvider>
      <AppContent userSearch={userSearch} setUserSearch={setUserSearch} />
    </FilterProvider>
  );
}

export default App;
