import React from 'react';
import './cart.css';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, clearCart } from './reducer';
import ScrollToTopButton from './scrolltop';

function Cart() {
    const dispatch = useDispatch();
    const cartItems = useSelector((state) => state.cartItems);

    // Calculate total price considering quantity
    let totalPrice = cartItems.reduce((acc, item) => {
        return acc + item.Price * item.Quantity;
    }, 0);

    let discount = 0; // No discount

    if (totalPrice > 1000) {
        discount = totalPrice * 0.1; // 10% discount
    }
    
    const totalAmount = totalPrice - discount; // Assuming discount is subtracted from totalPrice

    const handleClearCart = () => {
        // Dispatch action to clear the cart
        dispatch(clearCart());
    };

    return (
        <>
        <div className="cart-container">
            <div className="cart-header">
                <h2>Shopping Cart</h2>
                <p>Number of items: {cartItems.length}</p>
            </div>
            <div className="cart-items">
                {cartItems.map((item, i) => (
                    <div className="cart-item" key={i}>
                        <img src={item.Img} alt={item.Name} />
                        <div className="cart-item-details">
                            <h5>{item.Name}</h5>
                            <p>{item.Description}</p>
                        </div>
                        <div className="cart-item-price">
                            <b>&#8377;</b> {item.Price}
                        </div>
                        <div className="cart-item-quantity">
                            <label>Quantity: {item.Quantity}</label>
                        </div>
                        <div className="cart-item-total">
                            Total: <b>&#8377;</b> Number({item.Price * item.Quantity})
                        </div>
                        <button
                            className="btn btn-danger" id = "removecart"
                            onClick={() => {
                                const quantityToRemove = parseInt(prompt(`Enter quantity to remove for ${item.Name}:`, item.Quantity));
                                if (!isNaN(quantityToRemove) && quantityToRemove > 0) {
                                    dispatch(removeFromCart({ index: i, quantity: quantityToRemove }));
                                }
                            }}
                        >
                            Remove From Cart
                        </button>
                    </div>
                ))}
            </div>
            <div className="cart-summary">
                <h4>Order Summary</h4>
                <div className="cart-summary-item">
                    Total Price: <b>&#8377;</b> {totalPrice}
                </div>
                <div className="cart-summary-item">
                    Discount: <b>&#8377;</b> {discount}<br/>
                    <em style = {{color : "red"}}>*(To avail the discount, the purchase price must exceed 999)</em>
                </div>
                <div className="cart-summary-item">
                    Total Amount: <b>&#8377;</b> {totalAmount}
                </div>
                <button className="order"  onClick={handleClearCart}>
                    Place Order
                </button>
            </div>
        </div>
    <ScrollToTopButton />
    </>
    );
}

export default Cart;
