import React from 'react';

const Location = ({ showModal, handleModalClose }) => {

  const handleChangeLocation = () => {
    // Close the modal completely
    handleModalClose();
  };

  return (
    <div>
      {showModal && (
        <div className="modal" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Choose Location</h5>
                <button type="button" className="btn-close" aria-label="Close" onClick={handleModalClose}></button>
              </div>
              <div className="modal-body">
                <form>
                  <div className="mb-3">
                    <label htmlFor="locationSelect" className="form-label">Select Location</label>
                    <select className="form-select" id="locationSelect" size="4">
                         <option>Madhapur</option>
                         <option>Sainikpuri</option>
                    </select>
                  </div>
                  <button className="btn btn-primary mt-2" id="searchres" type="button" onClick={handleChangeLocation}>Change Location</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Location;
