import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './components/header';
import { FilterProvider, useFilters } from './components/filtercontext';
import { styles } from '../src/App.css';
import Shimmer from './shimmer'

const AppContent = ({ userSearch, setUserSearch }) => {
  const { filters, applyFilters } = useFilters();
  const [restaurants, setRestaurants] = useState([]);
  const [location, setLocations] = useState({
    lat: "17.4375084",
    long: "78.4482441"
  });

  const [showModal, setShowModal] = useState(false); // State for modal

  useEffect(() => {
    axios.get(`https://www.swiggy.com/dapi/restaurants/list/v5?lat=${location.lat}&lng=${location.long}&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`)
      .then((res) => {
        const allRestaurants = res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants;
        setRestaurants(allRestaurants);
      });
  }, [location]);

  useEffect(() => {
    setRestaurants(prevRestaurants => applyFilters(prevRestaurants));
  }, [filters, applyFilters]);

  const handleModalClose = () => {
    setShowModal(false); // Close modal
  };

  return (
    <div className="container-fluid">
      <Header setUserSearch={setUserSearch} setShowModal={setShowModal} /> {/* Pass setShowModal to Header */}
      <div className="row">
        <div className="col-12">
          <Shimmer />
          <div className="row row-cols-1 row-cols-md-4 g-4">
          {restaurants.length == 0 ? <Shimmer /> : " "}
            {restaurants.map((item, i) => ( // Use restaurants directly, no need for filteredRestaurants
              <div className="col" key={i}>
                <div className="card h-100">
                  <img className='card-img-top' src={`https://media-assets.swiggy.com/swiggy/image/upload/f1_lossy,f_auto,w_660/${item.info.cloudinaryImageId}`} className="card-img-top" alt="..." />
                  <div className="card-body">
                    <h5 className="card-title">{item.info.name}</h5>
                    <p className="card-text">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                        <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                      </svg>
                      <span style={{ marginLeft: "5px" }}>{item.info.avgRating ? item.info.avgRating : item.avgRatingString}</span>
                      <span style={{ marginLeft: "25px" }}><i className="fa-solid fa-motorcycle"></i>{" " + item.info.sla.slaString}</span>
                    </p>
                    <p style={{ marginBottom: "0px" }} className='card-text'>{item.info.cuisines.slice(0, 3).join(" , ")}</p>
                    <p style={{ marginTop: "5px" }} className='card-text'>{item.info.areaName}</p>
                    <div className="d-flex justify-content-center">
                      <button className='menubtn' id="searchbtn">View Menu</button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* Modal */}
      {showModal && (
        <div className="modal" tabIndex="-1" role="dialog" style={{ display: 'block' }}>
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Search Locations Modal</h5>
                <button type="button" className="btn-close" aria-label="Close" onClick={handleModalClose}></button>
              </div>
              <div className="modal-body"  style={{ maxHeight: "200px", overflowY: "auto" }}>
                <div style={{textAlign : "center"}}>
                  <input placeholder='search locations' name = "locations"/>
                  <br/>
                  <br/>
                  <label>
                  <input type = "radio" onChange = {()=>{
                    setLocations({
                      lat : "17.4485835",
                      long : "78.39080349999999"
                    })
                  }} name = "locations"/>
                  Madhapur</label><br/>
                  <label>
                  <input type = "radio" onChange = {()=>{
                    setLocations({lat : "17.492838" ,  long : "78.54690149999999"})
                  }} name = "locations"/>
                  Sainikpuri</label>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

const App = () => {
  const [userSearch, setUserSearch, setLocations] = useState("");

  return (
    <FilterProvider>
      <AppContent userSearch={userSearch} setUserSearch={setUserSearch} setLocations={setLocations} />
    </FilterProvider>
  );
}

export default App;
