import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Shimmer from '../shimmer';

function Initialrestaurents({ location, setLocations }) {
  const [restaurants, setRestaurants] = useState([]);
  const [userSearch, setUserSearch] = useState("");
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);

  useEffect(() => {
    setRestaurants([]);
    setFilteredRestaurants([]);
    axios.get(`https://www.swiggy.com/dapi/restaurants/list/v5?lat=${location.lat}&lng=${location.long}&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`)
      .then((res) => {
        setRestaurants(res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
      });
  }, [location]);

  useEffect(() => {
    const filtered = restaurants.filter(item => {
      return item.info.name.toLowerCase().includes(userSearch.toLowerCase());
    });
    setFilteredRestaurants(filtered);
  }, [userSearch, restaurants]);

  const sortByCategory = (category) => {
    let sortedRestaurants = [...filteredRestaurants];
    if (category === "delivery" || category === "rating") {
      sortedRestaurants.sort((a, b) => {
        return category === "delivery" ? a.info.sla.deliveryTime - b.info.sla.deliveryTime : b.info.avgRating - a.info.avgRating;
      });
      setFilteredRestaurants(sortedRestaurants);
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center mt-4">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              value={userSearch}
              onChange={(e) => setUserSearch(e.target.value)}
              placeholder="Search restaurants by name"
            />
            <button className="btn btn-outline-secondary" type="button" onClick={() => setUserSearch("")}>
              <i className="fa fa-times"></i>
            </button>
          </div>
        </div>
        <div className="col-md-4">
          <select className="form-select" onChange={(e) => sortByCategory(e.target.value)}>
            <option value="delivery">Fast Delivery</option>
            <option value="rating">Rating High to Low</option>
          </select>
        </div>
      </div>
      <div className="row row-cols-1 row-cols-md-3 g-4 mt-2 mb-2">
        {filteredRestaurants.length > 0 && (
          filteredRestaurants.map((item, index) => (
            <div className="col" key={index}>
              <div className="card">
                <img src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_660/${item.info.cloudinaryImageId}`} className="card-img-top restaurentImage" alt="..." />
                <div className="card-body">
                  <h5 className="card-title">{item.info.name}</h5>
                  <p className="card-text">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                      <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                    </svg>
                    <span style={{ marginLeft: "5px" }}>{item.info.avgRating ? item.info.avgRating : item.info.avgRatingString}</span>
                    <span style={{ marginLeft: "25px" }}><i className="fa-solid fa-motorcycle"></i>{item.info.sla.slaString}</span></p>
                  <p style={{ marginBottom: "0px" }} className='card-text'>{item.info.cuisines.slice(0, 3).join(" | ")}</p>
                  <p style={{ marginTop: "5px" }} className='card-text'>{item.info.areaName}</p>
                </div>
              </div>
            </div>
          ))
        )}
        {filteredRestaurants.length === 0 && (
          <div className="col text-center">
            {restaurants.length === 0 && <Shimmer />}
            {restaurants.length > 0 && <p>No restaurants found</p>}
          </div>
        )}
      </div>
    </div>
  );
}

export default Initialrestaurents;
