import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { addToCart } from './reducer'; // Assuming this is the correct path to your addToCart action
import ScrollToTopButton from './scrolltop';

export default function SearchRestaurants() {
    const [restaurantName, setRestaurantName] = useState('');
    const [restaurants, setRestaurants] = useState([]);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const dispatch = useDispatch(); // Get dispatch function from Redux

    const handleClear = () => {
        setRestaurantName('');
    };

    useEffect(() => {
        if (restaurantName) {
            setLoading(true);
            axios
                .get(`https://www.swiggy.com/dapi/restaurants/search/v3?lat=17.37240&lng=78.43780&str=${restaurantName}&trackingId=undefined&submitAction=SUGGESTION&queryUniqueId=3445c27e-9767-0109-930c-a7c5b2183e33`)
                .then((res) => {
                    setRestaurants(res.data.data?.cards[1]?.groupedCard?.cardGroupMap?.RESTAURANT || []);
                })
                .finally(() => {
                    setLoading(false);
                });
        } else {
            setRestaurants([]);
        }
    }, [restaurantName]);

    const handleViewMenu = (restaurant) => {
        // Ensure `restaurant` object contains the correct nested restaurant info
        const restaurantInfo = restaurant?.card?.card?.info || restaurant?.info;

        // Dispatch action to add to cart
        dispatch(addToCart({
            RestaurantName: restaurantInfo.name,
            Img: `https://media-assets.swiggy.com/swiggy/image/upload/f_auto,q_auto,w_660/${restaurantInfo.cloudinaryImageId}`,
            Name: restaurantInfo.name,
            Description: restaurantInfo.cuisines.slice(0, 3).join(" , "),
            Price: 0, // Set an appropriate price if available
            Quantity: 1 // Set an initial quantity
        }));

        // Navigate to restaurant view or perform any other actions
        navigate(`/restaurant/${restaurantInfo.id}/${restaurantInfo.name}`);
    };

    return (
        <div style={{ textAlign: "center" }}>
            <h1>Search Restaurants</h1>
            <form className="d-flex justify-content-center" role="search">
                <div className="col-md-6 col-lg-4 m-2 clearable-input">
                    <input
                        className="form-control"
                        value={restaurantName}
                        onChange={(e) => {
                            setRestaurantName(e.target.value);
                        }}
                        type="search"
                        placeholder="Search Restaurants By Name"
                        aria-label="Search"
                    />
                </div>
            </form>
            <div className="container">
                <div className="row row-cols-1 row-cols-md-4 g-4">
                    {restaurants?.map((restaurant, i) => {
                        if (restaurant?.card?.card?.info) {
                            return (
                                <div className="col" key={i}>
                                    <div className="card h-100">
                                        <img className='card-img-top' src={`https://media-assets.swiggy.com/swiggy/image/upload/f_auto,q_auto,w_660/${restaurant?.card?.card?.info?.cloudinaryImageId}`} alt="Restaurant" />
                                        <div className="card-body">
                                            <h5 className="card-title">{restaurant?.card?.card?.info?.name}</h5>
                                            <p className="card-text">
                                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                                                    <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                                                </svg>
                                                <span style={{ marginLeft: "5px" }}>{restaurant?.card?.card?.info?.avgRating ? restaurant?.card?.card?.info?.avgRating : restaurant?.card?.card?.info?.avgRatingString}</span>
                                                <span style={{ marginLeft: "25px" }}><i className="fa-solid fa-motorcycle"></i>{" " + restaurant?.card?.card?.info?.sla.slaString}</span>
                                            </p>
                                            <p style={{ marginBottom: "0px" }} className='card-text'>{restaurant?.card?.card?.info?.cuisines.slice(0, 3).join(" , ")}</p>
                                            <p style={{ marginTop: "5px" }} className='card-text'>{restaurant?.card?.card?.info?.areaName}</p>
                                            <div className="d-flex justify-content-center">
                                                <button className='menubtn' id="searchbtn"
                                                    onClick={() => handleViewMenu(restaurant)}
                                                >View Menu</button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            );
                        } else if (restaurant?.card?.card?.restaurants) {
                            return (
                                <>
                                    {restaurant?.card?.card?.restaurants.map((item, j) => {
                                        return (
                                            <div className="col" key={j}>
                                                <div className="card h-100">
                                                    <img className='card-img-top' src={`https://media-assets.swiggy.com/swiggy/image/upload/f_auto,q_auto,w_660/${item?.info.cloudinaryImageId}`} alt="Restaurant" />
                                                    <div className="card-body">
                                                        <h5 className="card-title">{item?.info.name}</h5>
                                                        <p className="card-text">
                                                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                                                                <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                                                            </svg>
                                                            <span style={{ marginLeft: "5px" }}>{item?.info.avgRating ? item?.info.avgRating : item?.info.avgRatingString}</span>
                                                            <span style={{ marginLeft: "25px" }}><i className="fa-solid fa-motorcycle"></i>{" " + item?.info.sla.slaString}</span>
                                                        </p>
                                                        <p style={{ marginBottom: "0px" }} className='card-text'>{item?.info.cuisines.slice(0, 3).join(" , ")}</p>
                                                        <p style={{ marginTop: "5px" }} className='card-text'>{item?.info.areaName}</p>
                                                        <div className="d-flex justify-content-center">
                                                            <button className='menubtn' id="searchbtn"
                                                                onClick={() => handleViewMenu(item)}
                                                            >View Menu</button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        );
                                    })}
                                </>
                            );
                        }
                    })}
                </div>
            </div>
            <ScrollToTopButton />
        </div>
    );
}
