import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './components/header';
import Location from './components/Location';
import FilterContextProvider from './components/FilterContextProvider';

const App = () => {
  const [userSearch, setUserSearch] = useState("");
  const [restaurants, setRestaurants] = useState([]);
  const [location, setLocation] = useState({
    lat: "17.4375084",
    long: "78.4482441"
  });
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    axios.get(`https://www.swiggy.com/dapi/restaurants/list/v5?lat=${location.lat}&lng=${location.long}&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`)
      .then((res) => {
        const allRestaurants = res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants;
        setRestaurants(allRestaurants);
      });
  }, [location]);

  useEffect(() => {
    const filtered = restaurants.filter(restaurant =>
      restaurant.info.name.toLowerCase().includes(userSearch.toLowerCase())
    );
    setRestaurants(filtered);
  }, [userSearch, restaurants]);

  const handleModalClose = () => {
    setShowModal(false);
  };

  return (
    <div className="App">
      <FilterContextProvider>
        <Header setUserSearch={setUserSearch} />
        <Location
          showModal={showModal}
          handleModalClose={handleModalClose}
          setLocation={setLocation}
        />
        <div className="row">
          <div className="col-12">
            <div className="row row-cols-1 row-cols-md-4 g-4">
              {restaurants.map((item, i) => (
                <div className="col" key={i}>
                  <div className="card h-100">
                    <img
                      className='card-img-top'
                      src={`https://media-assets.swiggy.com/swiggy/image/upload/f1_lossy,f_auto,w_660/${item.info.cloudinaryImageId}`}
                      className="card-img-top"
                      alt="..."
                    />
                    <div className="card-body">
                      <h5 className="card-title">{item.info.name}</h5>
                      <p className="card-text">
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                          <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                        </svg>
                        <span>{item.info.avgRating ? item.info.avgRating : item.avgRatingString}</span>
                        <span>{item.info.sla.slaString}</span>
                      </p>
                      <p className='card-text'>{item.info.cuisines.slice(0, 3).join(" , ")}</p>
                      <p className='card-text'>{item.info.areaName}</p>
                      <div className="d-flex justify-content-center">
                        <button className='menubtn' id="searchbtn">View Menu</button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </FilterContextProvider>
    </div>
  );
}

export default App;
