        <div className="cart-container">
    <div className="cart-header">
        <h2>Shopping Cart</h2>
        <p>Number of items: {cartItems.length}</p>
    </div>
    <table className="cart-items">
        <thead>
            <tr>
                <th>Image</th>
                <th>Item Name</th>
                <th>Description</th>
                <th>Price (₹)</th>
                <th>Quantity</th>
                <th>Total (₹)</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
            {cartItems.map((item, i) => (
                <tr className="cart-item" key={i}>
                    <td><img src={item.Img} alt={item.Name} /></td>
                    <td>{item.Name}</td>
                    <td>{item.Description}</td>
                    <td><b>&#8377;</b> {item.Price}</td>
                    <td>Quantity: {item.Quantity}</td>
                    <td>Total: <b>&#8377;</b> {item.Price * item.Quantity}</td>
                    <td>
