import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './components/header'

function App() {
  let [restaurants, setRestaurants] = useState([]);
  let [userSearch, setUserSearch] = useState("");
  let [filteredRestaurants, setFilteredRestaurants] = useState([]);

  useEffect(() => {
    axios.get(`https://www.swiggy.com/dapi/restaurants/list/v5?lat=17.37240&lng=78.43780&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`)
      .then((res) => {
        setRestaurants(res.data.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants);
      });
  }, []);

  useEffect(() => {
    let result = restaurants.filter((item, i) => {
      if (item.info.name.toLowerCase().includes(userSearch.toLowerCase()) == true) {
        return true;
      }
    });
    setFilteredRestaurants(result);
  }, [userSearch]);

  return (
    <div className="container-fluid">
      <Header userSearch={userSearch} setUserSearch={setUserSearch} />
      <div className="row">
        <div className="col-12">
          <div className="dropdown d-block d-lg-none mt-3 text-center">
            <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
              Dropdown button
            </button>
            <ul className="dropdown-menu">
              <li><a className="dropdown-item" href="#">Action</a></li>
              <li><a className="dropdown-item" href="#">Another action</a></li>
              <li><a className="dropdown-item" href="#">Something else here</a></li>
            </ul>
          </div>
          <div className="row row-cols-1 row-cols-md-4 g-4">
            {filteredRestaurants.length > 0 ? filteredRestaurants?.map((item, i) => (
              <div className="col" key={i}>
                <div className="card h-100">
                  <img className="card-img-top" src={`https://media-assets.swiggy.com/swiggy/image/upload/f1_lossy,f_auto,w_660/${item.info.cloudinaryImageId}`} alt="Restaurant" />
                  <div className="card-body">
                    <h5 className="card-title">{item.info.name}</h5>
                    <p className="card-text">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                        <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                      </svg>
                      <span style={{ marginLeft: "5px" }}>{item.info.avgRating ? item.info.avgRating : item.avgRatingString}</span>
                      <span style={{ marginLeft: "25px" }}><i className="fa-solid fa-motorcycle"></i>{" " + item.info.sla.slaString}</span></p>
                    <p style={{ marginBottom: "0px" }} className='card-text'>{item.info.cuisines.slice(0, 3).join(" , ")}</p>
                    <p style={{ marginTop: "5px" }} className='card-text'>{item.info.areaName}</p>
                    <div className="d-flex justify-content-center">
                      <button className='menubtn'>View Menu</button>
                    </div>
                  </div>
                </div>
              </div>
            )) : restaurants?.map((item, i) => (
              <div className="col" key={i}>
                <div className="card h-100">
                  <img className="card-img-top" src={`https://media-assets.swiggy.com/swiggy/image/upload/f1_lossy,f_auto,w_660/${item.info.cloudinaryImageId}`} alt="Restaurant" />
                  <div className="card-body">
                    <h5 className="card-title">{item.info.name}</h5>
                    <p className="card-text">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-star-fill" viewBox="0 0 16 16">
                        <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z" />
                      </svg>
                      <span style={{ marginLeft: "5px" }}>{item.info.avgRating ? item.info.avgRating : item.avgRatingString}</span>
                      <span style={{ marginLeft: "25px" }}><i className="fa-solid fa-motorcycle"></i>{" " + item.info.sla.slaString}</span></p>
                    <p style={{ marginBottom: "0px" }} className='card-text'>{item.info.cuisines.slice(0, 3).join(" , ")}</p>
                    <p style={{ marginTop: "5px" }} className='card-text'>{item.info.areaName}</p>
                    <div className="d-flex justify-content-center">
                      <button className='menubtn'>View Menu</button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;



